mapper.select:
	select 

	from tr_access_db_record


      and id = #{id,jdbcType=VARCHAR}


      and salesarrivecode = #{salesarrivecode,jdbcType=VARCHAR}


      and icardcode = #{icardcode,jdbcType=VARCHAR}


      and intotime = #{intotime,jdbcType=VARCHAR}


      and rfid = #{rfid,jdbcType=VARCHAR}


      and accesstype = #{accesstype,jdbcType=VARCHAR}


      and businesstype = #{businesstype,jdbcType=VARCHAR}


      and source = #{source,jdbcType=VARCHAR}


      and state = #{state,jdbcType=VARCHAR}


      and creator = #{creator,jdbcType=VARCHAR}


      and createtime = #{createtime,jdbcType=BIGINT}


      and modifier = #{modifier,jdbcType=VARCHAR}


      and modifytime = #{modifytime,jdbcType=BIGINT}
