mapper.select:
select count(0) from tr_finance_db_customer_beginning


		and state = #{state,jdbcType=VARCHAR}


		and customername = #{customername,jdbcType=VARCHAR}


		and orgname like '%${orgname}%'


		and makebillname = #{makebillname,jdbcType=VARCHAR}


		and createtime >= #{starttime,jdbcType=BIGINT}


		and createtime < #{endtime,jdbcType=BIGINT}

  	select

  	from tr_finance_db_customer_beginning


  			and state = #{state,jdbcType=VARCHAR}


  			and customername = #{customername,jdbcType=VARCHAR}


  			and orgname like '%${orgname}%'


  			and makebillname = #{makebillname,jdbcType=VARCHAR}


  			and createtime >= #{starttime,jdbcType=BIGINT}


  			and createtime < #{endtime,jdbcType=BIGINT}


  	order by createtime desc


			limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}
