mapper.select:
  	select

  	from tr_access_db_record_copy


  	  	and code = #{code,jdbcType=VARCHAR}


  	  	and state = #{state,jdbcType=VARCHAR}




		  	and businesstype = #{businesstype,jdbcType=VARCHAR}


				and EXISTS(select id from tr_purchase_db_arrive p1 where p1.id=noticeid

					and p1.vehicleid = #{vehicleid,jdbcType=VARCHAR}


					and p1.icardno = #{icardno,jdbcType=VARCHAR}


					and EXISTS(select id from tr_purchase_db_application_detail p2 where p1.billdetailid=p2.id
							and p2.materielid = #{materielid,jdbcType=VARCHAR}
					)

				)


				and EXISTS(select id from tr_sales_db_arrive s1 where s1.id=noticeid

					and s1.vehicleid = #{vehicleid,jdbcType=VARCHAR}


					and p1.icardno = #{icardno,jdbcType=VARCHAR}


					and EXISTS(select id from tr_sales_db_application_detail s2 where s1.billid=s2.id
							and s2.materielid = #{materielid,jdbcType=VARCHAR}
					)

				)




	  	  	and (EXISTS(select id from tr_purchase_db_arrive p1 where p1.id=noticeid

					and p1.vehicleid = #{vehicleid,jdbcType=VARCHAR}


					and p1.icardno = #{icardno,jdbcType=VARCHAR}


					and EXISTS(select id from tr_purchase_db_application_detail p2 where p1.billdetailid=p2.id
							and p2.materielid = #{materielid,jdbcType=VARCHAR}
					)

			)
			or EXISTS(select id from tr_sales_db_arrive s1 where s1.id=noticeid

				and s1.vehicleid = #{vehicleid,jdbcType=VARCHAR}


					and p1.icardno = #{icardno,jdbcType=VARCHAR}


				and EXISTS(select id from tr_sales_db_application_detail s2 where s1.billid=s2.id
						and s2.materielid = #{materielid,jdbcType=VARCHAR}
				)

			))



  		and entertime >= #{starttime,jdbcType=BIGINT}


  		and entertime < #{endtime,jdbcType=BIGINT}


  	order by entertime desc


			limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

  	select count(0)
  	from tr_access_db_record_copy


  	  	and code = #{code,jdbcType=VARCHAR}


  	  	and state = #{state,jdbcType=VARCHAR}



		  	and businesstype = #{businesstype,jdbcType=VARCHAR}


				and EXISTS(select id from tr_purchase_db_arrive p1 where p1.id=noticeid

					and p1.vehicleid = #{vehicleid,jdbcType=VARCHAR}


					and p1.icardno = #{icardno,jdbcType=VARCHAR}


					and EXISTS(select id from tr_purchase_db_application_detail p2 where p1.billdetailid=p2.id
							and p2.materielid = #{materielid,jdbcType=VARCHAR}
					)

				)


				and EXISTS(select id from tr_sales_db_arrive s1 where s1.id=noticeid

					and s1.vehicleid = #{vehicleid,jdbcType=VARCHAR}


					and p1.icardno = #{icardno,jdbcType=VARCHAR}


					and EXISTS(select id from tr_sales_db_application_detail s2 where s1.billid=s2.id
							and s2.materielid = #{materielid,jdbcType=VARCHAR}
					)

				)




	  	  	and (EXISTS(select id from tr_purchase_db_arrive p1 where p1.id=noticeid

					and p1.vehicleid = #{vehicleid,jdbcType=VARCHAR}


					and p1.icardno = #{icardno,jdbcType=VARCHAR}


					and EXISTS(select id from tr_purchase_db_application_detail p2 where p1.billdetailid=p2.id
							and p2.materielid = #{materielid,jdbcType=VARCHAR}
					)

			)
			or EXISTS(select id from tr_sales_db_arrive s1 where s1.id=noticeid

					and s1.vehicleid = #{vehicleid,jdbcType=VARCHAR}


					and p1.icardno = #{icardno,jdbcType=VARCHAR}


					and EXISTS(select id from tr_sales_db_application_detail s2 where s1.billid=s2.id
							and s2.materielid = #{materielid,jdbcType=VARCHAR}
					)

			))



  		and entertime >= #{starttime,jdbcType=BIGINT}


  		and entertime < #{endtime,jdbcType=BIGINT}
