  			and billno like '%${billno}%'


  			and supplierid = #{supplierid,jdbcType=VARCHAR}


  			and source = #{source,jdbcType=VARCHAR}


  			and applytime >= #{starttime,jdbcType=BIGINT}


  			and applytime < #{endtime,jdbcType=BIGINT}




