mapper.select:
   select 

    from tr_pound_db_note

   		netweight > 0

			and weighttime >= #{beginTimeLong,jdbcType=BIGINT}


			and weighttime <= #{endTimeLong,jdbcType=BIGINT}


			and noticecode <= #{noticecodeLike,jdbcType=BIGINT}


			and enteryardname <= #{enteryardnameLike,jdbcType=BIGINT}


			and leaveyardname <= #{leaveyardnameLike,jdbcType=BIGINT}



			and suppliername like concat(#{supplierNameLike,jdbcType=VARCHAR},'%')


			and vehicleno like concat(#{vehicleNoLike,jdbcType=VARCHAR},'%')


			and customername like concat(#{customerNameLike,jdbcType=VARCHAR},'%')


			and drivername like concat(#{driverNameLike,jdbcType=VARCHAR},'%')


			and materialname like concat(#{cargoNameLike,jdbcType=VARCHAR},'%') 


			and remark like concat(#{remarkLike,jdbcType=VARCHAR},'%')


			and code like concat(#{codeLike,jdbcType=VARCHAR},'%')


			and billcode like concat(#{billcodeLike,jdbcType=VARCHAR},'%')


  			and billtype = #{billtype,jdbcType=VARCHAR}


  			and status = #{status,jdbcType=VARCHAR}


  	order by lighttime desc


			limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

   select 
    count(1)
    from tr_pound_db_note

   		netweight > 0

			and weighttime >= #{beginTimeLong,jdbcType=BIGINT}


			and weighttime <= #{endTimeLong,jdbcType=BIGINT}


			and noticecode <= #{noticecodeLike,jdbcType=BIGINT}


			and enteryardname <= #{enteryardnameLike,jdbcType=BIGINT}


			and leaveyardname <= #{leaveyardnameLike,jdbcType=BIGINT}


			and suppliername like concat(#{supplierNameLike,jdbcType=VARCHAR},'%')


			and vehicleno like concat(#{vehicleNoLike,jdbcType=VARCHAR},'%')


			and customername like concat(#{customerNameLike,jdbcType=VARCHAR},'%')


			and drivername like concat(#{driverNameLike,jdbcType=VARCHAR},'%')


			and materialname like concat(#{cargoNameLike,jdbcType=VARCHAR},'%') 


			and code like concat(#{codeLike,jdbcType=VARCHAR},'%')


			and billcode like concat(#{billcodeLike,jdbcType=VARCHAR},'%')


			and remark like concat(#{remarkLike,jdbcType=VARCHAR},'%')


  			and billtype = #{billtype,jdbcType=VARCHAR}


  			and status = #{status,jdbcType=VARCHAR}
