select `id` AS `id`, `code` AS `poundcode`, `supplierid` AS `supplierid`, `suppliername` AS `suppliername`, `minemouthid` AS `minemouthid`, `minemouthname` AS `minemouthname`, `yardid` AS `yardid`, `yardname` AS `yardname`, `materialid` AS `materialid`, `materialname` AS `materialname`, `vehicleid` AS `vehicleid`, `vehicleno` AS `vehicleno`, `grossweight` AS `grossweight`, `tareweight` AS `tareweight`, `netweight` AS `netweight`, `originalnetweight` AS `originalnetweight`, `lighttime` AS `lighttime`, `weighttime` AS `weighttime`, `createtime` AS `createtime`, `createtime` AS `billtime`, `receiverpersonname` AS `signPersonName`, `createtime` AS `signTime`, `drivername` AS `drivername`, `driverid` AS `driverid`, `cargo` AS `cargo`, `returnstatus` AS `returnstatus`, `status` AS `status` from tr_pound_db_note
status=1 and billtype in ('0','1')
and lighttime >= #{beginTimeLong,jdbcType=BIGINT}
and lighttime < #{endTimeLong,jdbcType=BIGINT}
and suppliername like concat(#{supplierNameLike,jdbcType=VARCHAR},'%')
and vehicleno like concat(#{vehicleNoLike,jdbcType=VARCHAR},'%')
and minemouthname like concat(#{minemouthNameLike,jdbcType=VARCHAR},'%')
and drivername like concat(#{driverNameLike,jdbcType=VARCHAR},'%')
and materialname like concat(#{cargoNameLike,jdbcType=VARCHAR},'%')
and remark like concat(#{remarkLike,jdbcType=VARCHAR},'%')
and returnstatus = #{returnstatus,jdbcType=VARCHAR}
and yardname = #{yardname,jdbcType=VARCHAR}
order by lighttime desc
limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

SELECT suppliername , minemouthname, materialname, yardname, count(vehicleno) as countVehicleno, sum(grossweight) as sumGrossweight, sum(tareweight) as sumTareweight, sum(netweight) as sumNetweight FROM v_report_purchase1
netweight>0 and minemouthname is not NULL
and lighttime >= #{beginTimeLong,jdbcType=BIGINT}
and lighttime < #{endTimeLong,jdbcType=BIGINT}
and suppliername like concat(#{supplierNameLike,jdbcType=VARCHAR},'%')
and vehicleno like concat(#{vehicleNoLike,jdbcType=VARCHAR},'%')
and minemouthname like concat(#{minemouthNameLike,jdbcType=VARCHAR},'%')
and drivername like concat(#{driverNameLike,jdbcType=VARCHAR},'%')
and materialname like concat(#{cargoNameLike,jdbcType=VARCHAR},'%')
and remark like concat(#{remarkLike,jdbcType=VARCHAR},'%')
and returnstatus = #{returnstatus,jdbcType=VARCHAR}
and yardname = #{yardname,jdbcType=VARCHAR}
GROUP BY supplierid, minemouthname, yardname, materialname
ORDER BY supplierid
limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

