mapper.select:
select

from tr_sales_db_assay_report

	state = "1"

		and starttime >=#{starttime,jdbcType=BIGINT} 


		and starttime <= #{endtime,jdbcType=BIGINT} 


		and code=#{code,jdbcType=VARCHAR}


		and batchnumid=#{batchnumid,jdbcType=VARCHAR}


		and reporttype=#{reporttype,jdbcType=VARCHAR}


order by createtime desc

	limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

select
count(1)
from tr_sales_db_assay_report

	state = "1"

		and createtime >=#{starttime,jdbcType=BIGINT} 


		and createtime <= #{endtime,jdbcType=BIGINT} 


		and code=#{code,jdbcType=VARCHAR}


		and batchnumid=#{batchnumid,jdbcType=VARCHAR}


		and reporttype=#{reporttype,jdbcType=VARCHAR}
