mapper.select:
    select 

    from tr_system_user


        and code like CONCAT('%',#{codeLike},'%')  


        and account like CONCAT('%',#{accountLike},'%')  


        and name like CONCAT('%',#{nameLike},'%')  


        and code = #{code}


        and account = #{account}


   	order by utc desc


			limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}
